#ifndef CATMrshlDebug_H
#define CATMrshlDebug_H

#include "DSYSysTraceSystemMigration.h"

#ifdef DSY_SYS_TRACE_SYSTEM_MIGRATION
/************************************************************************/
/* Defines                                                              */
/************************************************************************/

/************************************************************************/
/* Global includes                                                      */
/************************************************************************/

/************************************************************************/
/* Local includes                                                       */
/************************************************************************/

/************************************************************************/
/* Functions declaration                                                */
/************************************************************************/

//=======================================================================
//==[ marshal.cpp]=======================================================
//=======================================================================
extern int* S_MarshalTraceActive;
//=======================================================================

//=======================================================================
//==[ marshal.cpp]=======================================================
//=======================================================================
extern int S_Marshal64;
//=======================================================================

//=======================================================================
// Trace object for "Marshal64Trace" log.
//=======================================================================
static DSYSysTrace* s_pMarshal64Trace = NULL;
//=======================================================================

/************************************************************************/
/************************************************************************/

//=======================================================================
inline void __TRAOPEN(void)
//=======================================================================
{
  if ( s_pMarshal64Trace == NULL )
  {
    s_pMarshal64Trace = new DSYSysTrace(MARSHAL64_DEBUG_STRG);
    if ( s_pMarshal64Trace != NULL )
    {
      S_Marshal64 = MARSHAL64_DEBUG_FLAG;
      S_MarshalTraceActive = (int *)s_pMarshal64Trace;
    }
  }
}

//=======================================================================
inline DSYSysTrace* __TRAPRINT(int iFileDescriptor)
//=======================================================================
{
  register DSYSysTrace* l_pTrace = NULL;

  switch( iFileDescriptor )
  {
  case MARSHAL64_DEBUG_FLAG:
    {
      l_pTrace = s_pMarshal64Trace;
    }
    break;
  default:
    { }
  }

  // Done.
  return l_pTrace;
}
#else // DSY_SYS_TRACE_SYSTEM_MIGRATION
# include "CATTraMacro.h"
# include "CATTrace.h"
#endif  // DSY_SYS_TRACE_SYSTEM_MIGRATION
#endif  // CATMrshlDebug_H
