#ifndef CATOmxJsonSchemaResolver_H
#define CATOmxJsonSchemaResolver_H

#include "CATOMY.h"
#include "CATOmxJsonObject.h"
#include "CATOmxArray.h"
#include "CATUnicodeString.h"
#include "CATOmxKeyString.h"

class ExportedByCATOMY CATOmxJsonSchemaResolver
{
public:
  /*
  * Returns TRUE if the given reference can be resolved,
  * which mean the reference format is known by this resolver.
  */
  virtual CATBoolean IsReferenceCanBeResolved(CATUnicodeString& iReference) = 0;

  /*
  * Resolves the given reference and returns the resolved schema and/or errors if something went wrong.
  * Must return S_OK if the schema has been resolved without error.
  * Must return E_FAIL if the schema cannot be resolved and put errors in the dedicacted output parameter.
  */
  virtual HRESULT ResolveReference(CATUnicodeString& iReference, CATOmxJsonObject& oResolvedSchema, CATOmxArray<CATUnicodeString>& oErrors) = 0;
};

#endif
