class CATPLMCEStampProxy
{
  CATPLMCEStamp _cestamp;
  
public :
  CATPLMCEStampProxy(const CATPLMCEStamp& cestamp):
       _cestamp(cestamp)
  {
  }
  operator CATPLMCEStamp()
  {
    return _cestamp;
  }

  CATPLMCEStamp & operator *()
  {
    return _cestamp;
  }

  CATPLMCEStampProxy():_cestamp(CATPLMCEStampServices::CreateInvalidCEStamp())
  {;}

};

