#define	CATPLMNBFILTERTYPE	  4

/** Mask of Attribute filter */
#define	CATPLMMaskFilterAttribute	(1 << 0)//1
/** Mask of volume filter */
#define	CATPLMMaskFilterVolume     (1 << 1)//2
/** Mask of relation filter */
#define	CATPLMMaskFilterRelation	(1 << 2)//4
