#ifndef CATPLMSynchronizationStrategy_H
#define CATPLMSynchronizationStrategy_H

/**
* Definition of the possible refresh strategies.
* <tt> Possible values:</tt>
* <dl>
* @param <tt>CATPLMSynchronizationStrategy_Synchronize</tt>     <dd>Component has no changes in session. Integrates remote iteration.
* @param <tt>CATPLMSynchronizationStrategy_Forget</tt>          <dd>Component has changes in session. Forgets changes and integrates remote iteration.
* @param <tt>CATPLMSynchronizationStrategy_Exclude</tt>         <dd>Component has or has no changes in session. The concurrency is not resolved.
* </dl>
*/
typedef unsigned int CATPLMSynchronizationStrategy;

#define CATPLMSynchronizationStrategy_None              0x00000000
#define CATPLMSynchronizationStrategy_All               0xFFFFFFFF
#define CATPLMSynchronizationStrategy_Synchronize       0x00000001
#define CATPLMSynchronizationStrategy_Forget            0x00000002
#define CATPLMSynchronizationStrategy_Exclude           0x00000004
#define CATPLMSynchronizationStrategy_Lock              0x00000008

#endif
