#define DECLARE_SYP_PROJECT(var) \
class ExportedByUIVCoreTools CATSYPProject_##var \
{          \
public: \
  CATSYPProject_##var() {}; \
  ~CATSYPProject_##var() {}; \
  static int GetActiveValue() ;\
  static void SetActiveValue(const int & value);\
private: \
  static int _activeValue; \
};
#define IMPL_SYP_PROJECT(var,antiVar,level,versionning,useWhiteList) \
int CATSYPProject_##var::_activeValue = -1; \
int CATSYPProject_##var::GetActiveValue() \
{ \
  if (_activeValue==-1) _activeValue = CheckActivationVariableForLevel((char*) #var,(char*) #antiVar,level,versionning,useWhiteList); \
  static CATBoolean newRecord = ShouldRecordVersion("UIV_CHANGE_ACTIVATE_VALUE",1) ; \
  if (newRecord) \
    return _activeValue ==1; \
  return _activeValue; \
} \
void CATSYPProject_##var::SetActiveValue(const int &value) \
{ \
  _activeValue = value;\
}


