#ifndef CATXMLXSLTransformerOptions_h
#define CATXMLXSLTransformerOptions_h

// System framework
#include "CATUnicodeString.h"

// XMLParser framework
#include "CatXmlItfExt.h"

// Standard SAX2 features

/**
 * Class to group constants which correspond to the standard SAX2 features and properties.
 */
class ExportedByCatXmlItfExt CATXMLXSLTransformerOptions {
	public:
		/**
		 * Option to control formatting of XML generated by a CATIXMLXSLTransformer.
		 * Can be <tt>yes</tt> (indent the generated XML) or <tt>no</tt> (do not indent the
		 * generated XML).
		 * Default value is <tt>no</tt>.
		 */
		static CATUnicodeString Indent;

		/**
		 * Option to specify the format of the output of a CATIXMLXSLTransformer.
		 * Possible values are <tt>xml</tt>, <tt>html</tt> and <tt>text</tt>.
		 * Default value is <tt>xml</tt>.
		 */
		static CATUnicodeString OutputFormat;

	private:
		/**
		 * Make the class non instantiable
		 */
		CATXMLXSLTransformerOptions();
};

#endif // CATXMLXSLTransformerOptions_h
