#ifndef CATlsoGTAColor_H
#define CATlsoGTAColor_H

// GTA debug color define
// CATCGMDebug::PutColor(...)
// 
// R :: red   [0,15]
// G :: green [0,15]
// B :: blue  [0,15]

// =========================
//   Pairing Face 
// =========================

// Perfect pairing : sky blue
#define GTACOLOR_PAIRING_PERFECT_R   8
#define GTACOLOR_PAIRING_PERFECT_G  12
#define GTACOLOR_PAIRING_PERFECT_B  13


// Final pairing : light blue
#define GTACOLOR_PAIRING_FINAL_R   0
#define GTACOLOR_PAIRING_FINAL_G   8
#define GTACOLOR_PAIRING_FINAL_B  15


// Confirmed pairing : Orange
#define GTACOLOR_PAIRING_CONFIRMED_R  15
#define GTACOLOR_PAIRING_CONFIRMED_G   8
#define GTACOLOR_PAIRING_CONFIRMED_B   0


// Complex pairing : Yellow
#define GTACOLOR_PAIRING_COMPLEX_R  15
#define GTACOLOR_PAIRING_COMPLEX_G  15
#define GTACOLOR_PAIRING_COMPLEX_B   0


// Unlinked face : purple
#define GTACOLOR_PAIRING_UNLINKED_R  11
#define GTACOLOR_PAIRING_UNLINKED_G   5
#define GTACOLOR_PAIRING_UNLINKED_B  12

// =========================
//   Lateral Face
// =========================

// Admitted lateral face : black
#define GTACOLOR_LATERALFACE_ADMITTED_R   0
#define GTACOLOR_LATERALFACE_ADMITTED_G   0
#define GTACOLOR_LATERALFACE_ADMITTED_B   0


// Candidate lateral face : yellow
#define GTACOLOR_LATERALFACE_CANDIDATE_R   15
#define GTACOLOR_LATERALFACE_CANDIDATE_G   15
#define GTACOLOR_LATERALFACE_CANDIDATE_B    0


// Refused lateral face : red
#define GTACOLOR_LATERALFACE_REFUSED_R   15
#define GTACOLOR_LATERALFACE_REFUSED_G    0
#define GTACOLOR_LATERALFACE_REFUSED_B    0


// Pairing lateral face : green
#define GTACOLOR_LATERALFACE_PAIRING_R    0
#define GTACOLOR_LATERALFACE_PAIRING_G   10 
#define GTACOLOR_LATERALFACE_PAIRING_B    0

// Square lateral face : dark red
#define GTACOLOR_LATERALFACE_SQUARE_R    9
#define GTACOLOR_LATERALFACE_SQUARE_G    0 
#define GTACOLOR_LATERALFACE_SQUARE_B    4

// =========================
//   Lateral Edge
// =========================

#endif

