#ifndef EKDeprecated_H
#define EKDeprecated_H

#if defined(_MSC_VER) && (_MSC_VER >= 1500)
# define EK_DEPRECATED_IMPL(msg, decl) __declspec(deprecated(msg)) decl
#else
# define EK_DEPRECATED_IMPL(msg, decl) decl
#endif

#define EK_DEPRECATED(date, msg, decl) EK_DEPRECATED_IMPL("\n" \
                                                          "###############################################################################\n" \
                                                          "###########################  EXECUTION  FRAMEWORK  ############################\n" \
                                                          "################################  DEPRECATED  #################################\n" \
                                                          "###############################################################################\n" \
                                                          "#########################  please migrate your code   #########################\n" \
                                                          "######################### deprecated since " date " #########################\n" \
                                                          "###############################################################################\n" \
                                                          "###############################################################################\n" \
                                                          "##\n" \
                                                          "## " msg "\n" \
                                                          "##\n" \
                                                          "###############################################################################\n" \
                                                          "###############################################################################\n" \
                                                          , decl)

#endif /*EKDeprecated_H*/
