#ifndef OOCPCHRFILESTREAM_H
#define OOCPCHRFILESTREAM_H

#include "OOCPCHRESULT.h"

enum class OOCPCFileStreamCode : std::uint32_t
{
    eERROR_SEEK_FAIL = static_cast<std::uint32_t>( OOCPCCode::eCOMPONENT_SPECIFIC_START ),
    eERROR_CLOSE_FAIL,
    eERROR_MEMORY_MAPPING_FAIL,
    eERROR_FLUSH_FAIL,
    eERROR_READ_FAIL,
    eERROR_WRITE_FAIL,
    eERROR_INVALID_CACHE,
    eERROR_CONCURRENT_CALL,
    eERROR_MISSING_BLOCK
};

static constexpr HRESULT OOCPCFILESTREAM_E_SEEK_FAIL = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eFILESTREAM, OOCPCFileStreamCode::eERROR_SEEK_FAIL );
static constexpr HRESULT OOCPCFILESTREAM_E_CLOSE_FAIL = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eFILESTREAM, OOCPCFileStreamCode::eERROR_CLOSE_FAIL );
static constexpr HRESULT OOCPCFILESTREAM_E_MEMORY_MAPPING_FAIL = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eFILESTREAM, OOCPCFileStreamCode::eERROR_MEMORY_MAPPING_FAIL );
static constexpr HRESULT OOCPCFILESTREAM_E_FLUSH_FAIL = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eFILESTREAM, OOCPCFileStreamCode::eERROR_FLUSH_FAIL );
static constexpr HRESULT OOCPCFILESTREAM_E_READ_FAIL = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eFILESTREAM, OOCPCFileStreamCode::eERROR_READ_FAIL );
static constexpr HRESULT OOCPCFILESTREAM_E_WRITE_FAIL = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eFILESTREAM, OOCPCFileStreamCode::eERROR_WRITE_FAIL );
static constexpr HRESULT OOCPCFILESTREAM_E_INVALID_CACHE = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eFILESTREAM, OOCPCFileStreamCode::eERROR_INVALID_CACHE );
static constexpr HRESULT OOCPCFILESTREAM_E_CONCURRENT_CALL = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eFILESTREAM, OOCPCFileStreamCode::eERROR_CONCURRENT_CALL );
static constexpr HRESULT OOCPCFILESTREAM_E_MISSING_BLOCK = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eFILESTREAM, OOCPCFileStreamCode::eERROR_MISSING_BLOCK );
static constexpr HRESULT OOCPCFILESTREAM_E_UNEXPECTED = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eFILESTREAM, OOCPCCode::eUNEXPECTED );
static constexpr HRESULT OOCPCFILESTREAM_E_OPEN_FAIL = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eFILESTREAM, OOCPCCode::eOPEN_FAILED );
static constexpr HRESULT OOCPCFILESTREAM_E_FILE_NOT_FOUND = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eFILESTREAM, OOCPCCode::eFILE_NOT_FOUND );
static constexpr HRESULT OOCPCFILESTREAM_E_NOTIMPL = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eFILESTREAM, OOCPCCode::eNOTIMPL );

#ifdef OOCPC_USE_APPLY_LIST
#define OOCPC_APPLY_LIST_TO_HRFILESTREAM \
    OOCPC_APPLY(OOCPCFILESTREAM_E_SEEK_FAIL), \
    OOCPC_APPLY(OOCPCFILESTREAM_E_CLOSE_FAIL), \
    OOCPC_APPLY(OOCPCFILESTREAM_E_MEMORY_MAPPING_FAIL), \
    OOCPC_APPLY(OOCPCFILESTREAM_E_FLUSH_FAIL), \
    OOCPC_APPLY(OOCPCFILESTREAM_E_READ_FAIL), \
    OOCPC_APPLY(OOCPCFILESTREAM_E_WRITE_FAIL), \
    OOCPC_APPLY(OOCPCFILESTREAM_E_INVALID_CACHE), \
    OOCPC_APPLY(OOCPCFILESTREAM_E_CONCURRENT_CALL), \
    OOCPC_APPLY(OOCPCFILESTREAM_E_MISSING_BLOCK), \
    OOCPC_APPLY(OOCPCFILESTREAM_E_UNEXPECTED), \
    OOCPC_APPLY(OOCPCFILESTREAM_E_OPEN_FAIL), \
    OOCPC_APPLY(OOCPCFILESTREAM_E_FILE_NOT_FOUND), \
    OOCPC_APPLY(OOCPCFILESTREAM_E_NOTIMPL)
#endif  // OOCPC_USE_APPLY_LIST

#endif  //ifndef OOCPCHRFILESTREAM_H
