#ifndef OOCPCHRNODECACHE_H
#define OOCPCHRNODECACHE_H

#include "OOCPCHRESULT.h"

enum class OOCPCNodeCacheCode : std::uint32_t
{
    eERROR_MAX_NODE_DEPTH = static_cast<std::uint32_t>( OOCPCCode::eCOMPONENT_SPECIFIC_START ),
    eERROR_NODE_WITHOUT_MATCHING_SLICE,
    eERROR_INVALID_NODE_ID,
    eERROR_INVALID_NODE_OOC_ID,
    eERROR_INVALID_SLICE_ID,
    eERROR_NON_SLICE_LEAF,
    eERROR_SLICE_BUILDER_FAILED,
    eERROR_INVALID_COORDINATE_SYSTEM,
    eERROR_INVALID_ITERATION_INDEX,
    eERROR_INVALID_NODE_TYPE,
    eERROR_INVALID_ACCEL_CONT,
    eERROR_UNPROCESSED_NODE_LEFT
};

static constexpr HRESULT OOCNODECACHE_E_MAX_NODE_DEPTH = MAKE_OOCPC_HRESULT(SEVERITY_ERROR, OOCPCFacility::eNODECACHE, OOCPCNodeCacheCode::eERROR_MAX_NODE_DEPTH);
static constexpr HRESULT OOCNODECACHE_E_NODE_WITHOUT_MATCHING_SLICE = MAKE_OOCPC_HRESULT(SEVERITY_ERROR, OOCPCFacility::eNODECACHE, OOCPCNodeCacheCode::eERROR_NODE_WITHOUT_MATCHING_SLICE);
static constexpr HRESULT OOCNODECACHE_E_INVALID_NODE_ID = MAKE_OOCPC_HRESULT(SEVERITY_ERROR, OOCPCFacility::eNODECACHE, OOCPCNodeCacheCode::eERROR_INVALID_NODE_ID);
static constexpr HRESULT OOCNODECACHE_E_INVALID_NODE_OOC_ID = MAKE_OOCPC_HRESULT(SEVERITY_ERROR, OOCPCFacility::eNODECACHE, OOCPCNodeCacheCode::eERROR_INVALID_NODE_OOC_ID);
static constexpr HRESULT OOCNODECACHE_E_INVALID_SLICE_ID = MAKE_OOCPC_HRESULT(SEVERITY_ERROR, OOCPCFacility::eNODECACHE, OOCPCNodeCacheCode::eERROR_INVALID_SLICE_ID);
static constexpr HRESULT OOCNODECACHE_E_NON_SLICE_LEAF = MAKE_OOCPC_HRESULT(SEVERITY_ERROR, OOCPCFacility::eNODECACHE, OOCPCNodeCacheCode::eERROR_NON_SLICE_LEAF);
static constexpr HRESULT OOCNODECACHE_E_SLICE_BUILDER_FAILED = MAKE_OOCPC_HRESULT(SEVERITY_ERROR, OOCPCFacility::eNODECACHE, OOCPCNodeCacheCode::eERROR_SLICE_BUILDER_FAILED);
static constexpr HRESULT OOCNODECACHE_E_INVALID_COORDINATE_SYSTEM = MAKE_OOCPC_HRESULT(SEVERITY_ERROR, OOCPCFacility::eNODECACHE, OOCPCNodeCacheCode::eERROR_INVALID_COORDINATE_SYSTEM);
static constexpr HRESULT OOCNODECACHE_E_INVALID_ITERATION_INDEX = MAKE_OOCPC_HRESULT(SEVERITY_ERROR, OOCPCFacility::eNODECACHE, OOCPCNodeCacheCode::eERROR_INVALID_ITERATION_INDEX);
static constexpr HRESULT OOCNODECACHE_E_INVALID_NODE_TYPE = MAKE_OOCPC_HRESULT(SEVERITY_ERROR, OOCPCFacility::eNODECACHE, OOCPCNodeCacheCode::eERROR_INVALID_NODE_TYPE);
static constexpr HRESULT OOCNODECACHE_E_UNPROCESSED_NODE_LEFT = MAKE_OOCPC_HRESULT(SEVERITY_ERROR, OOCPCFacility::eNODECACHE, OOCPCNodeCacheCode::eERROR_UNPROCESSED_NODE_LEFT);
static constexpr HRESULT OOCNODECACHE_E_INVALID_ACCEL_CONT = MAKE_OOCPC_HRESULT(SEVERITY_ERROR, OOCPCFacility::eNODECACHE, OOCPCNodeCacheCode::eERROR_INVALID_ACCEL_CONT);
static constexpr HRESULT OOCNODECACHE_E_ABORT = MAKE_OOCPC_HRESULT(SEVERITY_ERROR, OOCPCFacility::eNODECACHE, OOCPCCode::eABORT);

#ifdef OOCPC_USE_APPLY_LIST
#define OOCPC_APPLY_LIST_TO_HRNODECACHE \
    OOCPC_APPLY(OOCNODECACHE_E_MAX_NODE_DEPTH), \
    OOCPC_APPLY(OOCNODECACHE_E_NODE_WITHOUT_MATCHING_SLICE), \
    OOCPC_APPLY(OOCNODECACHE_E_INVALID_NODE_ID), \
    OOCPC_APPLY(OOCNODECACHE_E_INVALID_NODE_OOC_ID), \
    OOCPC_APPLY(OOCNODECACHE_E_INVALID_SLICE_ID), \
    OOCPC_APPLY(OOCNODECACHE_E_NON_SLICE_LEAF), \
    OOCPC_APPLY(OOCNODECACHE_E_SLICE_BUILDER_FAILED), \
    OOCPC_APPLY(OOCNODECACHE_E_INVALID_COORDINATE_SYSTEM), \
    OOCPC_APPLY(OOCNODECACHE_E_INVALID_ITERATION_INDEX), \
    OOCPC_APPLY(OOCNODECACHE_E_INVALID_NODE_TYPE), \
    OOCPC_APPLY(OOCNODECACHE_E_UNPROCESSED_NODE_LEFT), \
    OOCPC_APPLY(OOCNODECACHE_E_INVALID_ACCEL_CONT), \
    OOCPC_APPLY(OOCNODECACHE_E_ABORT )
#endif  // OOCPC_USE_APPLY_LIST

#endif  //ifndef OOCPCHRNODECACHE_H
