#ifndef OOCPCHRQUERY_H
#define OOCPCHRQUERY_H

#include "OOCPCHRESULT.h"

enum class OOCPCQueryCode : std::uint32_t
{
    eERROR_NO_OCTREE_CACHE = static_cast<std::uint32_t>( OOCPCCode::eCOMPONENT_SPECIFIC_START ),
    eERROR_NO_QUERY,
    eERROR_NO_CHILD_NODE,
    eERROR_NO_BOOLEAN_AST,
    eERROR_INVALID_BOOLEAN_AST,
    eERROR_MISSING_METADATA
};

static constexpr HRESULT OOCPCQUERY_E_NO_OCTREE_CACHE = MAKE_OOCPC_HRESULT(SEVERITY_ERROR, OOCPCFacility::eQUERIES, OOCPCQueryCode::eERROR_NO_OCTREE_CACHE);
static constexpr HRESULT OOCPCQUERY_E_NO_QUERY = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eQUERIES, OOCPCQueryCode::eERROR_NO_QUERY );
static constexpr HRESULT OOCPCQUERY_E_NO_CHILD_NODE = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eQUERIES, OOCPCQueryCode::eERROR_NO_CHILD_NODE );
static constexpr HRESULT OOCPCQUERY_E_NO_BOOLEAN_AST = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eQUERIES, OOCPCQueryCode::eERROR_NO_BOOLEAN_AST );
static constexpr HRESULT OOCPCQUERY_E_INVALID_BOOLEAN_AST = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eQUERIES, OOCPCQueryCode::eERROR_INVALID_BOOLEAN_AST );
static constexpr HRESULT OOCPCQUERY_E_MISSING_METADATA = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eQUERIES, OOCPCQueryCode::eERROR_MISSING_METADATA );
static constexpr HRESULT OOCPCQUERY_E_NOTIMPL = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eQUERIES, OOCPCCode::eNOTIMPL );
static constexpr HRESULT OOCPCQUERY_E_UNEXPECTED = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eQUERIES, OOCPCCode::eUNEXPECTED );
static constexpr HRESULT OOCPCQUERY_E_INVALID_PARAMETER = MAKE_OOCPC_HRESULT( SEVERITY_ERROR, OOCPCFacility::eQUERIES, OOCPCCode::eINVALID_PARAMETER);

#ifdef OOCPC_USE_APPLY_LIST
#define OOCPC_APPLY_LIST_TO_HRQUERY \
    OOCPC_APPLY(OOCPCQUERY_E_NO_OCTREE_CACHE), \
    OOCPC_APPLY(OOCPCQUERY_E_NO_QUERY), \
    OOCPC_APPLY(OOCPCQUERY_E_NO_CHILD_NODE), \
    OOCPC_APPLY(OOCPCQUERY_E_NO_BOOLEAN_AST), \
    OOCPC_APPLY(OOCPCQUERY_E_INVALID_BOOLEAN_AST), \
    OOCPC_APPLY(OOCPCQUERY_E_MISSING_METADATA), \
    OOCPC_APPLY(OOCPCQUERY_E_NOTIMPL), \
    OOCPC_APPLY(OOCPCQUERY_E_UNEXPECTED), \
    OOCPC_APPLY(OOCPCQUERY_E_INVALID_PARAMETER)
#endif  // OOCPC_USE_APPLY_LIST

#endif  //ifndef OOCPCHRQUERY_H
