#ifndef OOCPCHRSTRINGREPOSITORY_H
#define OOCPCHRSTRINGREPOSITORY_H

#include "OOCPCHRESULT.h"

enum class OOCPCStringRepositoryCode : std::uint32_t
{
    eERROR_NON_EMPTY_POINTCLOUD = static_cast<std::uint32_t>( OOCPCCode::eCOMPONENT_SPECIFIC_START ),
    eERROR_BAD_METADATA,
};

static constexpr HRESULT OOCPCSTRINGREPOSITORY_E_NON_EMPTY_POINTCLOUD = MAKE_OOCPC_HRESULT(SEVERITY_ERROR, OOCPCFacility::eSTRINGREPOSITORY, OOCPCStringRepositoryCode::eERROR_NON_EMPTY_POINTCLOUD);
static constexpr HRESULT OOCPCSTRINGREPOSITORY_E_BAD_METADATA = MAKE_OOCPC_HRESULT(SEVERITY_ERROR, OOCPCFacility::eSTRINGREPOSITORY, OOCPCStringRepositoryCode::eERROR_BAD_METADATA);

#ifdef OOCPC_USE_APPLY_LIST
#define OOCPC_APPLY_LIST_TO_HRSTRINGREPOSITORY  \
    OOCPC_APPLY(OOCPCSTRINGREPOSITORY_E_NON_EMPTY_POINTCLOUD), \
    OOCPC_APPLY(OOCPCSTRINGREPOSITORY_E_BAD_METADATA)
#endif  // OOCPC_USE_APPLY_LIST

#endif  //ifndef OOCPCHRSTRINGREPOSITORY_H
