#ifndef PLMExchangeExperienceDesignProperties_H
#define PLMExchangeExperienceDesignProperties_H

/*========= How to build a design property  ==========================================================
 * Concatenate with the '|' operator, a property type, target and id :
 * ie. (DesignPropertyType_Integer | DesignPropertyId_Status_ColorIndexRed | DesignPropertyTarget_WIN)
/ ====================================================================================================*/

//========= Property value type ==============================================
//============================================================================
#define DesignPropertyType_Integer                          0x01000000
#define DesignPropertyType_String                           0x02000000
#define DesignPropertyType_Date                             0x04000000
#define DesignPropertyType_List                             0x08000000

//========= Property target ==================================================
//============================================================================
#define DesignPropertyTarget_WIN                            0x00100000
#define DesignPropertyTarget_INTERNAL                       0x00200000

//========= Property id ======================================================
//============================================================================
//=== Common Used by multiple type  ===========================================
#define DesignPropertyId_ShortHelp                          0x00000002        // String , NLS key : ShortHelp
#define DesignPropertyId_LongHelp                           0x00000004        // String , NLS key : LongHelp
#define DesignPropertyId_Icon                               0x00000800        // String , RSC key : Icon
#define DesignPropertyId_Name                               0x00000080        // String , NLS key : Name

//=========Property defined by object ========================================
//=== [ Execution ] ==========================================================
// DesignPropertyId_Name                                                      // String  , NLS key : Name  , Set the execution displayed name.
#define DesignPropertyId_ProgressIcon                       0x00001000        // String  , RSC key : ProgressIcon , Set the execution icon to display in the progress bar.
#define DesignPropertyId_ProgressCatalog                    0x00002000        // String  , RSC key : ProgressCatalog , Name of the NLS catalog (without its file extension) to used with the execution progress bar.
                                                                              //                     In the given catalog, these NLS keys must defined : ProgressTaskUI.Title, ProgressTaskUI.ObjectName

//=== [ Session Object ] =========================================================
#define DesignPropertyId_Object_Description                 0x00000200        // List of string  , NLS key : Description
// DesignPropertyId_Icon                                                      // List of string  , RSC key : Icon

//=== [ Operation State ] =========================================================
// DesignPropertyId_Name                                                       // String , NLS key : Name
// DesignPropertyId_Icon                                                       // String , RSC key : Icon

//=== [ Group ] ===========================================================
// DesignPropertyId_ShortHelp                                                  // String , NLS key : ShortHelp
// DesignPropertyId_LongHelp                                                   // String , NLS key : LongHelp
// DesignPropertyId_Icon                                                       // String , NLS key : Icon
#define DesignPropertyId_NotApplicable                      0x00000400         // String , NLS key : NotApplicable

//=== [Option ] ================================================================
// DesignPropertyId_ShortHelp                                                  // String , NLS key : ShortHelp
// DesignPropertyId_LongHelp                                                   // String , NLS key : LongHelp
#define DesignPropertyId_Browser_IsApplied                  0x00040000         // Integer, NLS key : IsApplied
#define DesignPropertyId_Browser_DisplayMode                0x00080000         // String , NLS key : DisplayMode : Default or UpPathWithoutFormat or UpPathWithoutButtons @see PLMExchangeExperienceBrowser.sypstyle

//=== [ Definition ] ============================================================
// This is will be used for the : panel title and status legend title.
#define DesignPropertyId_Definition_Title                   0x00000001        // String , NLS key : Title

//=== [ Status ] ================================================================
// Used to defined the coloration of a status by color index and transparency.
#define DesignPropertyId_Status_ColorIndexRed               0x00000008        // Integer , RSC key : IndexRed
#define DesignPropertyId_Status_ColorIndexGreen             0x00000010        // Integer , RSC key : IndexGreen
#define DesignPropertyId_Status_ColorIndexBlue              0x00000020        // Integer , RSC key : IndexBlue
#define DesignPropertyId_Status_ColorTransparency           0x00000040        // Integer , RSC key : Transparency
#define DesignPropertyId_Status_WhenApplied                 0x00000100        // String  , NLS key : WhenApplied  , Set the word describing the status when applied on an object.
// DesignPropertyId_Name                                                      // String  , NLS key : Name         , Set the status displayed name.
// DesignPropertyId_Icon                                                      // String  , RSC key : Icon

//-----------------------------------------------------------------------

#endif
