/**
 * @level Protected
 * @usage U1
 */

#ifndef PLMRepLoadMode_H
#define PLMRepLoadMode_H

//------------------------------------------------------------------

/**
 * Loading modes values.
 *
 * @param
 *    PLMMode: The Representation Reference has no Stream Descriptor loaded.
 * @param
 *    VisuMode: The Representation Reference meets the following conditions:
 *			<ul>
 *         <li>The Representation Reference is a 3D Representation</li>
 *				 <li>and</li>
 *					 <ul>
 *						 <li>has its AuthoringVisu Stream Descriptor loaded (in this case the loaded document is a CGR document)</li>
 *						 <li>or has its Authoring Stream Descriptor loaded and the loaded document can neither be edited nor linked</li>
 *			     </ul>
 *      </ul>
 * @param
 *    LinkableMode: The Representation Reference meets the following conditions:
 *      <ul>
 *	       <li>The Representation Reference is a 3D Representation</li>
 *	       <li>and has its Authoring Stream Descriptor loaded and the loaded document can be linked but not edited.</li>
 *      </ul>
 * @param
 *    EditMode: The Representation Reference has its Authoring Stream Descriptor loaded
 *				      and the loaded document can be edited.
 * @param
 *    DMUMode: The Representation Reference must meet the following conditions :
 *      <ul>
 *	       <li>The Representation Reference is a 3D Representation</li>
 *	       <li>and has its VisuRep Stream Descriptor loaded (in this case the loaded document is a CGR document)</li>
 *      </ul>
 *      The Representation Reference must be opened in a Browse mode session.
 * @param
 *    UVMode: The Representation Reference meets the following conditions:
 *			<ul>
 *         <li>The Representation Reference is a 3D Representation</li>
 *				 <li>and</li>
 *					 <ul>
 *						 <li>has its AuthoringVisu2 Stream Descriptor loaded if it exist (in this case the loaded document is a UVR document)</li>
 *						 <li>or it follow the VisuMode rule</li>
 *			     </ul>
 *      </ul>
 */
enum PLMRepLoadMode { PLMMode = 0, VisuMode = 1, LinkableMode = 2, EditMode = 3, DMUMode = 4, UVMode = 5 };

#endif
