#ifndef SWXAutoDeleteArrayPtr_H
#define SWXAutoDeleteArrayPtr_H


template <typename T> class SWXAutoDeleteArrayPtr {
public:
	SWXAutoDeleteArrayPtr()		: mPtr(NULL){}
	SWXAutoDeleteArrayPtr(T* iPtr) : mPtr(iPtr){}
	SWXAutoDeleteArrayPtr<T> &operator=(T *iPtr)
    {
        if (mPtr) delete [] mPtr;
        mPtr = iPtr;
        return *this;
    }
	virtual ~SWXAutoDeleteArrayPtr() {if (mPtr) delete[] mPtr; mPtr=NULL;}
	operator T*()   {return mPtr;}
	T* operator->() {return mPtr;}
	T* GetPtr() {return mPtr;}
	bool operator!() {return mPtr==NULL;}
public:
	T* mPtr;
};

#endif //
