#ifndef SWXAutoDeletePtr_H
#define SWXAutoDeletePtr_H


template <typename T> class SWXAutoDeletePtr {
public:
	SWXAutoDeletePtr()		: mPtr(NULL){}
	SWXAutoDeletePtr(T* iPtr) : mPtr(iPtr){}
	SWXAutoDeletePtr<T> &operator=(T *iPtr)
    {
        if (mPtr != iPtr )  delete mPtr ;
        mPtr = iPtr;
        return *this;
    }
	virtual ~SWXAutoDeletePtr() {if (mPtr) delete mPtr; mPtr=NULL;}
	operator T*()   {return mPtr;}
	T* operator->() {return mPtr;}
	T* GetPtr() {return mPtr;}
	bool operator!() {return mPtr==NULL;}
public:
	T* mPtr;
};

#endif //
