#ifndef SWXAutoPtr_H
#define SWXAutoPtr_H


template <typename T> class SWXAutoPtr {
public:
	SWXAutoPtr()		: mPtr(NULL){}
	SWXAutoPtr(T* iPtr) : mPtr(iPtr){}
	SWXAutoPtr<T> &operator=(T *iPtr)
    {
        if (mPtr) mPtr->Release();
        mPtr = iPtr;
        return *this;
    }
	virtual ~SWXAutoPtr() {if (mPtr) mPtr->Release(); mPtr=NULL;}
	operator T*()   {return mPtr;}
	T* operator->() {return mPtr;}
	T* GetPtr() {return mPtr;}
	bool operator!() {return mPtr==NULL;}
public:
	T* mPtr;
};

#endif //
