#ifndef SWXSvHelpData_H
#define SWXSvHelpData_H

class SWXSvHelpData
{
public:
	SWXSvHelpData(double iX, double iY, double iZ) : mX(iX), mY(iY), mZ(iZ), mHelpDataSet(true) {}
	SWXSvHelpData() : mX(0.0), mY(0.0), mZ(0.0), mHelpDataSet(false) {}

	double GetX() {return mX;}
	double GetY() {return mY;}
	double GetZ() {return mZ;}
	double GetU() {return mX;}
	double GetV() {return mY;}

	void SetX(double iIn) {mX=iIn;}
	void SetY(double iIn) {mY=iIn;}
	void SetZ(double iIn) {mZ=iIn;}
	void SetU(double iIn) {mX=iIn;}
	void SetV(double iIn) {mY=iIn;}

	bool IsHelpDataSet() {return mHelpDataSet;}
	void SetHelpDataIsSet(bool iFlag) {mHelpDataSet=iFlag;}

private:
	double mX;	// also used for U
	double mY;	// also used for V
	double mZ;
	bool mHelpDataSet;
};

#endif
