	void SetConfigID(int iConfigID);
	void GetConfigID(int& oConfigID);
	void SetConfigName(const CATUnicodeString& iConfigName);
	void GetConfigName(CATUnicodeString& oConfigName);

	void SetOwner(const CATUnicodeString& iOwner);
	void GetOwner(CATUnicodeString& oOwner);

	void SetFileName(const CATUnicodeString& iFileName);
	void GetFileName(CATUnicodeString& oFileName);

	void SetPathOnDisc(const CATUnicodeString& iPathOnDisc);
	void GetPathOnDisc(CATUnicodeString& oPathOnDisc);

	void SetCollabSpaceName(const CATUnicodeString& iCollabSpaceName);
	void GetCollabSpaceName(CATUnicodeString& oCollabSpaceName);

