#ifndef SWXUTLESS_H
#define SWXUTLESS_H

#include "SWXUtBinaryFunction.h"

template<class TYPE> struct SWXUtLess : public SWXUtBinaryFunction<TYPE, TYPE, bool>
{	
	bool operator()(const TYPE& iLeft, const TYPE& iRight) const
	{	
		return (iLeft < iRight) ? true : false;
	}
};

#endif
