#ifndef SWXUtUniqueIdFactory_H
#define SWXUtUniqueIdFactory_H

#include "SWXUtUniqueId.h"

class SWXUtUniqueIdFactory
{
public:
	SWXUtUniqueIdFactory() : mNextNumber(0) {}
	SWXUtUniqueId CreateUniqueId() {return SWXUtUniqueId(mNextNumber++);}
	SWXUtUniqueId CreateInvalidId() {return SWXUtUniqueId(-1);}
private:
	int mNextNumber;
};

#endif
