#ifndef UIScriptBaseBoxing_H
#define UIScriptBaseBoxing_H

#include "UIScriptBase.h"

// IntrospectionInfrastructure
#include <CATSYPDeclareBoxing.h>
#include <CATSYPBoxingComparator.h>
#include <CATSYPHashMap.h>

// ECMAScriptEngine
#include <ESValue.h>

typedef ES::Value ESValue;

/**
 * Specialization of CATSYPBoxingComparator for ES::Value.
 * See CATSYPBoxingComparator documentation for details.
 */
template <> struct CATSYPBoxingComparator<ESValue>
{
  int AreEqual(const ESValue& i_value1, const ESValue& i_value2) {
    if (!i_value1.IsValued() || !i_value2.IsValued())
      return 0;

    return i_value1.StrictEquals(i_value2);
  }
};

CATSYP_DECLARE_BOXING(ExportedByUIScriptBase, ESValue);

#endif
