#!/bin/ksh
#############################################################################
# COPYRIGHT DASSAULT SYSTEMES 2004                                          #
# ==========================================================================#
# usage: CSYSysKllNs.sh id
# ==========================================================================#
os=`uname 2> /dev/null` 
if [ $os = 'HP-UX' ] ; then
PATH=$PATH:/usr/contrib/bin/X11
fi
xkill -id 0x$1>/dev/null 2>&1
exit $?
